mapper.select:

SELECT c.no , p.title , p.contents
  FROM user u , category c , post p
 WHERE 1 =1
   AND u.id = #{id}
   AND c.no = #{no}
 LIMIT 0 , 1


SELECT p.no , p.title , p.contents, p.reg_date , p.category_no , c.blog_id
  FROM post p , category c
 WHERE 1=1
   AND p.category_no=c.no
   AND c.blog_id=#{id}
   AND p.category_no=#{no}


SELECT p.no , p.title , p.contents, p.reg_date , p.category_no
  FROM post p , category c
 WHERE 1 = 1
   AND p.category_no = c.no
   AND c.blog_id = #{id}
   AND p.no = #{no}
